SELECT nombre FROM producto;
SELECT nombre, precio FROM producto;
SELECT * FROM producto;
SELECT nombre, precio, (precio * 1.21) FROM producto;
SELECT nombre AS 'nom de producto', precio AS 'euro', (precio * 1.21) AS 'dolars' FROM producto;
SELECT upper(nombre) as 'nom', precio FROM producto;
SELECT lower(nombre) as 'nom', precio FROM producto;
SELECT nombre, upper(LEFT(nombre, 2)) as 'nom' FROM fabricante;
SELECT nombre, round(precio, 1) FROM producto;
SELECT nombre, truncate(precio, 0) FROM producto;
