SELECT producto.codigo, producto.nombre, fabricante.codigo, fabricante.nombre FROM producto, fabricante WHERE producto.codigo_fabricante = fabricante.codigo ORDER BY producto.precio DESC LIMIT 1;
SELECT nombre FROM producto WHERE (SELECT codigo FROM fabricante WHERE nombre = 'Lenovo') = codigo_fabricante;
SELECT nombre FROM producto WHERE (SELECT codigo FROM fabricante WHERE nombre = 'Crucial') = codigo_fabricante AND precio > 200;
SELECT nombre FROM producto WHERE (SELECT codigo FROM fabricante WHERE nombre = 'Asus') = codigo_fabricante OR (SELECT codigo FROM fabricante WHERE nombre = 'Hewlett-Packard') = codigo_fabricante OR (SELECT codigo FROM fabricante WHERE nombre = 'Seagate') = codigo_fabricante;
